pages.schema:
{
  "@context": "https://schema.org",
  "@type": "HowTo",
  "name": "How to Convert Images with CovertConvert",
  "description": "Convert HEIC, WebP, AVIF, and other image formats to JPG or PNG entirely in your browser.",
  "step": [
    {
      "@type": "HowToStep",
      "name": "Select Files",
      "text": "Drop image files onto the converter or click to browse. Supports HEIC, WebP, AVIF, TIFF, PNG, BMP, and GIF."
    },
    {
      "@type": "HowToStep",
      "name": "Choose Output Format",
      "text": "Select JPEG for smaller files or PNG for lossless quality. Adjust JPEG quality if desired."
    },
    {
      "@type": "HowToStep",
      "name": "Conversion Happens Locally",
      "text": "Your browser processes the images using WebAssembly. Files never leave your device."
    },
    {
      "@type": "HowToStep",
      "name": "Download Results",
      "text": "Converted files download automatically. Multiple files are bundled in a ZIP archive."
    }
  ]
}
